(1, 50, 'Día Naranja Bike Park', 'Aprovecha este viernes 01 de diciembre y ven al mejor Bike Park del sur de Chile. \n\nTicket día Naranja: $10.000. \n\n*Andarivel Tata.', '', '', 2, '', '/imagenes/modulos/bike-park/secciones/interna_1511357770.png', 0, 'dia-naranja-bike-park', 0, NULL, 11, '', '', '', '', NULL, '', NULL, NULL, NULL, NULL),
(1, 51, 'Training Camp Nevados de Chillán 2018', 'Supera tus límites con los mejores instructores! \nDesde el 29 de enero hasta el 2 de febrero\nHabitación Cuádruple <strong>$495.000</strong>\nHabitación Doble <strong>$557.500b</strong>\nAprovecha el <strong>10% dcto. para alumnos inscritos en 2017</strong>', '', '', 2, '/imagenes/modulos/bike-park/secciones/interna_1517231604.jpg', '/imagenes/modulos/bike-park/secciones/interna_1511449552.jpg', 0, 'training-camp-nevados-de-chillan-2018', 0, NULL, 11, '', '', '', '', NULL, '', NULL, NULL, NULL, NULL),
